import React from 'react';
import { GoMail } from 'react-icons/go';
import { FaRegClock } from 'react-icons/fa';
import { useState } from 'react';
import Link from 'next/link';
import Router from 'next/router';
import NProgress from 'nprogress';
import { APP_NAME } from '../../config';
import { signout, isAuth } from '../../actions/auth';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from 'reactstrap';
import Search from './../blog/Search';

const Uheader = (props) => {
  return (
      <Nav style={{alignItems: 'center', backgroundColor:'#001f24'}} >
        <NavItem >
          <NavLink style={{color:'white'}} href="#">Notícias & Mídia</NavLink>
        </NavItem>
        <Nav style={{color:'white', marginLeft:'40%'}}>
        <NavItem>
          <NavLink style={{color:'white'}} disabled href="#"><GoMail /> &nbsp;contato@dental04.com</NavLink>
        </NavItem>
        <NavItem>
          <NavLink  style={{color:'white'}} disabled href="#"><FaRegClock /> &nbsp;Seg-Sáb: 09h-18h</NavLink>
        </NavItem>
        <NavItem>
          <NavLink style={{color:'white'}} disabled href="#">PT-BR</NavLink>
        </NavItem>
        </Nav>
        
        {!isAuth() && (
              <React.Fragment>
                <NavItem>
                  <Link href="/signin">
                    <NavLink style={{color:'blue'}}>Entrar</NavLink>
                  </Link>
                </NavItem>
            
                <NavItem>
                  <Link href="/signup">
                    <NavLink style={{color:'blue'}} >Cadastrar</NavLink>
                  </Link>
                </NavItem>
              </React.Fragment>
            )}

            {isAuth() && isAuth().role === 0 && (
              <NavItem>
                <Link href="/user">
                  <NavLink style={{color:'blue'}}>{`${isAuth().name}'s Dashboard`}</NavLink>
                </Link>
              </NavItem>
            )}

            {isAuth() && isAuth().role === 1 && (
              <NavItem>
                <Link href="/admin">
                  <NavLink style={{color:'blue'}}>{`${isAuth().name}'s Dashboard`}</NavLink>
                </Link>
              </NavItem>
            )}

            {isAuth() && (
              <NavItem>
                <NavLink style={{color:'blue', cursor: 'pointer' }} onClick={() => signout(() => Router.replace(`/signin`))}>
                  Signout
                </NavLink>
              </NavItem>
            )}
      </Nav>
  );
}

export default Uheader;
