import React from 'react';
import { GoMail } from 'react-icons/go';
import { FaRegClock } from 'react-icons/fa';
import { useState } from 'react';
import Link from 'next/link';
import Router from 'next/router';
import NProgress from 'nprogress';
import { APP_NAME } from '../../config';
import { signout, isAuth } from '../../actions/auth';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from 'reactstrap';
import Search from './../blog/Search';

const Uheader = (props) => {
  
  return (
    
    <React.Fragment>
      <Navbar style={{ fontSize: '13px', lineHeight:'15px', backgroundColor:'#001f24'}} expand="md">
        <Link href="/">
          <NavLink style={{color:'white'}}>Notícias &  Mídia</NavLink>
        </Link>
        
        <NavbarToggler  />
        <Collapse navbar>
          <Nav className="ml-auto" navbar style={{marginRight:'80px'}}>
            <Nav >
            <NavItem>
                  <Link href="/signin">
                    <NavLink  style={{color:'white'}}><GoMail style={{margin:'0 3'}}/> contato@dental04.com</NavLink>
                  </Link>
                </NavItem>
            
                <NavItem>
                  <Link href="#">
                    <NavLink style={{ color:'white'}}><FaRegClock style={{margin:'0 3 3'}} /> Seg-Sab: 09h-18h</NavLink>
                  </Link>
                </NavItem>

                <NavItem>
                  <Link href="/signin">
                    <NavLink  style={{color:'white', marginRight:'40px'}}>PT-BR</NavLink>
                  </Link>
                </NavItem>
            </Nav>
            
            {!isAuth() && (
              <Nav >
                <NavItem>
                  <Link href="/signin">
                    <NavLink  style={{color:'white'}}>Entrar</NavLink>
                  </Link>
                </NavItem>
            
                <NavItem>
                  <Link href="/signup">
                    <NavLink style={{color:'white'}}>Cadastrar</NavLink>
                  </Link>
                </NavItem>
              </Nav>
            )}

            {isAuth() && isAuth().role === 0 && (
              <NavItem>
                <Link href="/user">
                  <NavLink style={{color:'gold'}}>{`${isAuth().name}'s Dashboard`}</NavLink>
                </Link>
              </NavItem>
            )}

            {isAuth() && isAuth().role === 1 && (
              <NavItem>
                <Link href="/admin">
                  <NavLink style={{color:'gold'}}>{`${isAuth().name}'s Dashboard`}</NavLink>
                </Link>
              </NavItem>
            )}

            {isAuth() && (
              <NavItem>
                <NavLink style={{color:'gray', cursor: 'pointer' }} onClick={() => signout(() => Router.replace(`/signin`))}>
                  Signout
                </NavLink>
              </NavItem>
            )}
          </Nav>
        </Collapse>
       
      </Navbar>
      
    </React.Fragment>
  );
};

export default Uheader;
